CREATE TABLE `zs_gmuni_content` (
  `id` varchar(50) COLLATE utf8_bin NOT NULL,
  `title` varchar(300) COLLATE utf8_bin NOT NULL COMMENT '正文标题',
  `content` longtext COLLATE utf8_bin NOT NULL COMMENT '正文内容，富文本',
