  `update_time` timestamp NULL DEFAULT NULL COMMENT '修改时间',
  `sort` decimal(10,0) DEFAULT NULL COMMENT '排序号',
  `top` char(10) COLLATE utf8_bin DEFAULT '0' COMMENT '0:否 1:是',
  `description` varchar(300) COLLATE utf8_bin DEFAULT NULL COMMENT '描述',
  `image_ids` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '保存正文中的所有图片id',
  `attachment_name` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

